package com.example.tenx.test272.Activity;

import android.os.Bundle;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.bumptech.glide.request.RequestOptions;
import com.example.tenx.test272.R;

public class FragmentWorkshops extends Fragment{
    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_workshops, container, false);


        ImageView testImg = view.findViewById(R.id.image_workshops1);
        String testUrl = "data:image/png;base64,";
        Glide.with(getActivity()).load(testUrl).apply(new RequestOptions().centerCrop()).into(testImg);

        return view;
    }
}
